###author: Vaibhav Sharma#Date: 10/23/2016#Description: Plots Accuracies#Output: None##function plotAccuraciesAndCosts(trainingAcc,validAcc,testAcc,numEpochs,trainingCost,validCost,testCost)  y = vertcat(trainingAcc,validAcc,testAcc);  y = y';  x = 1: numEpochs;  subplot (2, 1, 1);  plot (x, y(:,1:1), "r;Training Accuracy;", x, y(:,2:2), "g;Validation Accuracy;",x, y(:,3:3), "b;Test Accuracy;");  xlabel ("Epochs");  ylabel ("Accuracies");    z = vertcat(trainingCost,validCost,testCost);  z = z';  k = 1: numEpochs;  subplot (2, 1, 2);  plot (k, z(:,1:1), "r;Training Cost;", k, z(:,2:2), "g;Validation Cost;",k, z(:,3:3), "b;Test Cost;");  xlabel ("Epochs");  ylabel ("Costs");endfunction