###author: Vaibhav Sharma#Date: 10/22/2016#Description: Shuffles the data and returns the shuffled data#Output: Returns shuffled data##function [input,target,validInput,validTarget,testInput,testTarget] = shuffle(input,target,splitInfo)  inputRows = size(input,1);  inputCols = size(input,2);    # Verically concatenating the two matrices  temp = vertcat(input,target);    tempRows = size(temp,1);  tempCols = size(temp,2);    # shuffling step  temp = temp(:,randperm(tempCols));  # Separating input from target  input = temp(1:inputRows,:);  target = temp(inputRows+1:tempRows,:);      iCol = size(input,2);    trainSize = splitInfo(1);  validSize = splitInfo(2);  testSize = splitInfo(3);    numRowsValid = max(1,round(iCol * validSize / 100));  numRowsTest = max(1,round(iCol * testSize / 100));  numRowsTrain = iCol - numRowsValid - numRowsTest;  validInput = input(:,numRowsTrain+1:numRowsTrain+numRowsValid);  validTarget = target(:,numRowsTrain+1:numRowsTrain+numRowsValid);    testInput = input(:,numRowsTrain+numRowsValid+1:iCol);  testTarget = target(:,numRowsTrain+numRowsValid+1:iCol);    input = input(:,1:numRowsTrain);  target = target(:,1:numRowsTrain);  endfunction